import { Component } from '@angular/core';

@Component({
  selector: 'app-pop-upcomponent',
  templateUrl: './pop-upcomponent.component.html',
  styleUrls: ['./pop-upcomponent.component.scss']
})
export class PopUpcomponentComponent {
  popUpInputObject: any={
    logo :"",
    heading :"",
    question : "",
    name : "",
    conformationMessage : "",
    conformationMessageList  : [],
    submitButton : ""
  }

  ngOnInit(){
    this.popUpInputObject.logo="";
    this.popUpInputObject.heading ="Remove User";
    this.popUpInputObject.question ="Are you sure you want to Remove User";
    this.popUpInputObject.name ="G Phawhan Saii";
    this.popUpInputObject.conformationMessage ="By confirming this Anmuja Kumari woouldn't be able to:";
    this.popUpInputObject.conformationMessageList=[]
  }
}
