import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-pop-upcomponent',
  templateUrl: './pop-upcomponent.component.html',
  styleUrls: ['./pop-upcomponent.component.scss']
})
export class PopUpcomponentComponent {
  @Input() popUpInputObject: any;
  @Output() buttonStatusEmitter = new EventEmitter<string>();
  cancelButtonLabel : string = "Cancel";

  ngOnInit(){
    // console.log(this.popUpInputObject);
  }

  sendButtonStatus(status:any){
    this.buttonStatusEmitter.emit(status);
    console.log("sendStatus value",status);
  }
}
<div class="flex">
    <div class="pop_up_component">
        <div class="pop-up-header">
            <div><img [src]="popUpInputObject.logo" alt="{{popUpInputObject.heading}} is not found."></div> &nbsp;
            <div class="popup_heading">{{popUpInputObject.heading}}</div>
        </div>
        <div class="pop-up-body">
            <div>{{popUpInputObject.question}}?</div>
            <div>
                <b>{{popUpInputObject.name}}</b>
            </div>
            <div id="body-message">{{popUpInputObject.conformationMessage}}</div>
            <div *ngFor="let item of popUpInputObject.conformationMessageList; index as i">
                <div>{{i+1}}&nbsp; {{item}} </div>
            </div>
        </div>
        <div class="pop-up-button">
            <button class="button" (click)="sendButtonStatus(cancelButtonLabel)" >{{cancelButtonLabel}}</button>
            <button id="remove" (click)="sendButtonStatus(popUpInputObject.submitButton)">{{popUpInputObject.submitButton}}</button>
        </div>
    </div>
</div>
