  selector: 'app-toast',
  templateUrl: './toast.component.html',
  styleUrls: ['./toast.component.scss']
})
export class ToastComponent {
@Input() inputToToast?: toast;
error : boolean = false;
success : boolean = false;
info : boolean = false;



}
