import { Component } from '@angular/core';
import { headerList, popupData } from 'src/app/interfaces/interfaces';

@Component({
  selector: 'app-organizational-account',
  templateUrl: './organizational-account.component.html',
  styleUrls: ['./organizational-account.component.scss']
})
export class OrganizationalAccountComponent {

  data:headerList[]=[
    {
      id : 1,
      isSelected : true,
      label : 'Joined'
    },
    {
      id : 2,
      isSelected : false,
      label : 'Suggestions'
    }

  ];
selectedid:number = 1;
  getSelectedOne(item:headerList){
    console.log(item.label);
    this.selectedid = item.id;
  }

joinedList = [
  {id: 1, name: 'Anusha'},
  {id: 2, name: 'madhuri'}
];

suggestionList = [
  {id: 3, name: 'sindhu'},
  {id: 4, name: 'priya'}
]
showAlert : boolean = false;
member:any;
deleteClicked(member:any){
  this.AlertData[0].accountName = member.name;
  this.showAlert = true;
  this.member = member;

}

addClicked(member:any){
  this.joinedList.push(member);
  this.suggestionList.splice(member,1);
}

AlertData:popupData[]=[
  {
    warningName : 'Remove User',
    accountName : '',
    buttonName : 'Remove User',
    detailsList : [
      {sno:1,content:'Access any Projects related to this Accound'},
      {sno:2,content:'Any Personal Data Synced with this Account'}]
  }

];
clickedButtonName = 'Cancel'
getButtonName(clickedButtonName:any){
  this.clickedButtonName  = clickedButtonName;
  if(clickedButtonName == 'Cancel'){this.showAlert=false}
  else if(clickedButtonName == this.AlertData[0].buttonName){
    this.suggestionList.push(this.member);
    this.joinedList.splice(this.member,1);
    this.showAlert = false;
  }
}

inputToToast = {
  title : 'success',
  message : 'Successfully Removed the User'
}



}
