@Component({
  selector: 'app-toast',
  templateUrl: './toast.component.html',
  styleUrls: ['./toast.component.css']
})
export class ToastComponent {
  @Input() toastAction:any;
  @Input() toastEnable:any;
  n=setTimeout(() => {
    this.toastEnable=0;
  },3000);


}
