import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-toast-messages',
  templateUrl: './toast-messages.component.html',
  styleUrls: ['./toast-messages.component.scss']
})
export class ToastMessagesComponent {
  @Input() inputToToast : any;

  OnInit(){
  if(this.inputToToast.title == 'error'){
      this.inputToToast.message = ''
  }
}

}
<div class="toast" [ngClass]="{'error':inputToToast?.title=='Error', 'success':inputToToast?.title=='Successful', 'info':inputToToast?.title=='Information'}" >
    <div>
        <img [src]="inputToToast?.logo" alt="Logo is not found">
    </div>
    <div>
        <div>{{inputToToast?.title}}</div>
        <div>{{inputToToast?.message}}</div>
    </div>
</div>
