import { Component, EventEmitter, Input, Output } from '@angular/core';
import { popupData } from 'src/app/interfaces/interfaces';

@Component({
  selector: 'app-common-popup',
  templateUrl: './common-popup.component.html',
  styleUrls: ['./common-popup.component.scss']
})
export class CommonPopupComponent {
  @Input() dataToShowinPopup : any ;
  cancel = 'Cancel';
  @Output() clickedButtonName = new EventEmitter();

  // submit:boolean = true;
  actionButtonClicked(name:string){
    this.clickedButtonName.emit(name);
  }
  cancelButtonClicked(name:string){
    this.clickedButtonName.emit(name);
  }

}

//You can have to pass the following data in following way to use this common component.. i.e, by taking this interface as reference
//you can refer popupData interface in interfaces folder/ interfaces.ts file

// data:popupData[]=[
//   {
//     warningName : 'Remove User',
//     accountName : 'Anuja Kumari',
//     buttonName : 'Remove User',
//     detailsList : [
//       {sno:1,content:'Access any Projects related to this Accound'},
//       {sno:2,content:'Any Personal Data Synced with this Account'}]
//   }

// ];

<!-- The Modal -->
<div id="myModal" class="modal">
    <!-- [ngClass]="{'modal':submit,'notmodel':!submit}" -->
  <!-- Modal content -->
  <div class="modal__content">
    <!-- <span class="close">&times;</span> -->
    <div class="modal__content--header">
        <img src="../../../assets/images/svg/warningLogo.svg" alt="Warning">
        <span>{{dataToShowinPopup[0].warningName}}</span>
    </div>

    <div class="modal__content--info">
        <div class="warning--title">
        <span>Are you sure you want to {{dataToShowinPopup[0].warningName}}?</span>
        <div><strong>{{dataToShowinPopup.accountName}}</strong></div>
        </div>
        <div class="content--info--details">
            <span>By confirming this {{dataToShowinPopup[0].accountName}} wouldn't be able to:</span>
            <div *ngFor="let detail of dataToShowinPopup[0].detailsList">
                <span>{{detail.sno}}.{{detail.content}}</span>
            </div>
        </div>
        <div class="buttons--div">
            <button class="button" (click)="cancelButtonClicked(cancel)" >{{cancel}}</button>
            <button class="button" (click)="actionButtonClicked(dataToShowinPopup[0].buttonName)" >{{dataToShowinPopup[0].buttonName}}</button>
        </div>
    </div>
  </div>

</div>
