import { Component, EventEmitter, Input, Output } from '@angular/core';
import { headerList } from 'src/app/interfaces/interfaces';

@Component({
  selector: 'app-common-tabs',
  templateUrl: './common-tabs.component.html',
  styleUrls: ['./common-tabs.component.scss']
})
export class CommonTabsComponent {
  @Input() items : headerList [] = [];
  @Output() sendSelectedOne = new EventEmitter();

  selected(item:headerList){
    // this.items[].isSelected = false;
    console.log(item.id);
    item.isSelected = true;
    this.sendSelectedOne.emit(item);
  }
}
