import { Component, Input } from '@angular/core';
import { popupData } from 'src/app/interfaces/interfaces';

@Component({
  selector: 'app-common-popup',
  templateUrl: './common-popup.component.html',
  styleUrls: ['./common-popup.component.scss']
})
export class CommonPopupComponent {
  @Input() dataToShowinPopup : any ;

  warningName:string = '';
  accountName:string = 'Anuja kumari';
  detailsList=[
    {id : 1, content : "Access any Projects related to this Account"},
    {id : 2, content : "Any Personal Data Synced with this Account"}
  ]
  buttonName:string = 'Remove User';
}


