import { Component,EventEmitter,Input, Output } from '@angular/core';

@Component({
  selector: 'app-pop-ups',
  templateUrl: './pop-ups.component.html',
  styleUrls: ['./pop-ups.component.css']
})

export class PopUpsComponent {
  // @Input() removeUserName:any;
  @Input() alert:any;
  @Output() sendAction= new EventEmitter();
  pop=1;
  cancel(){
    this.pop=0;
  }
  action(event:any){
   this.sendAction.emit(event);
  }

}
<!-- <div *ngIf="pop" class="removePopUp">
    <header id="removePopUp__header">
        <img src="/assets/images/warning.svg"><span>Remove User</span>
    </header>
    <div>Are you sure want to Remove User?</div>
    <div><b>{{removeUserName}}</b></div>
    <div>
        <p>By confirming this <b>{{removeUserName}}</b> wouldn't be able to:</p>
        <ol>
            <li>Access any projects related to this Account.</li>
            <li>Any Personal Data linked with this Account.</li>
        </ol>
    </div>
    <div class="removePopUp__btn">
        <button class="cancel" (click)="cancel()">Cancel</button>
        <button class="remove">Remove User</button>
    </div>
</div>
 -->
 <div *ngIf="pop" class="removePopUp">
    <header id="removePopUp__header">
        <img src="/assets/images/warning.svg"><span>{{alert.heading}}</span>
    </header>
    <div>{{alert.question}}</div>
    <div><b>{{alert.userName}}</b></div>
    <div>
        <p>By confirming this <b>{{alert.userName}}</b> wouldn't be able to:</p>
        <ol>
            <li *ngFor="let i of alert.list">{{i}}</li>
        </ol>
    </div>
    <div class="removePopUp__btn">
        <!-- <button class="cancel" (click)="cancel()">Cancel</button>
        <button class="remove" (click)="action()">{{alert.action}}</button> -->
        <button *ngFor="let btn of alert.buttons" (click)="action(btn)">{{btn}}</button>
    </div>
</div>
