import { Component } from '@angular/core';

@Component({
  selector: 'app-tabs',
  templateUrl: './tabs.component.html',
  styleUrls: ['./tabs.component.css']
})
export class TabsComponent {
  activatedTab:number=0;
  tabslist=['NowShowing','UpComing Movies']
  setTab(i:number){
    this.activatedTab=i
  }
}
