import { Component } from '@angular/core';

@Component({
  selector: 'app-filter',
  templateUrl: './filter.component.html',
  styleUrls: ['./filter.component.css']
})
export class FilterComponent {

// data:any=[ {Languages:['English','Telugu','Tamil','Marati','Malayalam','kanada','punajabi','gujarati','bengali'];
//             Genere:['action','animation','biography','comedy','crime'] }]
  // keysOfUsersArray(){
  //   return Object.keys(this. data[0]) //return type Array
  // }
  accordionItems = [
    { Languages:['English','Telugu','Tamil','Marati','Malayalam','kanada','punajabi','gujarati','bengali'],   Genere:['action','animation','biography','comedy','crime']  },

  ];
  openAccordionIndex = -1;

  toggleAccordion(index: number) {
    if (this.isAccordionOpen(index)) {
      this.openAccordionIndex = -1;
    } else {
      this.openAccordionIndex = index;
    }
  }

  isAccordionOpen(index: number): boolean {
    return this.openAccordionIndex === index;
  }
}
